package semiTest;

public class Kimmyungjun {
	private String name;
	private int age;
	private double weight;
	private char gender;
	public Kimmyungjun() {
		// TODO Auto-generated constructor stub
	}
	public Kimmyungjun(String name, int age, double weight, char gender) {
		super();
		this.name = name;
		this.age = age;
		this.weight = weight;
		this.gender = gender;
	}
	public String getName() {
		return name;
	}
	public void setName(String name) {
		this.name = name;
	}
	public int getAge() {
		return age;
	}
	public void setAge(int age) {
		this.age = age;
	}
	public double getWeight() {
		return weight;
	}
	public void setWeight(double weight) {
		this.weight = weight;
	}
	public char getGender() {
		return gender;
	}
	public void setGender(char gender) {
		this.gender = gender;
	}
}
